mapper.select:
SELECT wt.businesskey AS Wf_Ref, mc.NAME_ AS Client_Name, mp.projectName AS Project_Name, mj.JOB_TYPE AS Job_Type, wt.taskName AS Task_Name, CASE WHEN wt.chargeStatus = 'Maintainance' THEN 'MNTN' WHEN wt.chargeStatus = 'Charged' THEN 'CHD' WHEN wt.chargeStatus = 'FOC' THEN 'FOC' ELSE 'NA' END AS Charge_Status, wt.allocatedTimeString AS Allocated_Time, CASE WHEN wa.assignedDeveloper_ID IS NOT NULL THEN CONCAT(au.FIRST_,' ',au.LAST_) ELSE '' END AS Assigned_Developer, wt.elapsedTimeString AS Elapsed_Time, wt.workCompletedDate AS Completed_Date, wt.submitionDate AS Due_Date, CASE WHEN wt.workType = 'Bug Fix'THEN 'BF' WHEN wt.workType = 'New Requirement'THEN 'NR' WHEN wt.workType = 'Change Logic' THEN 'CL' WHEN wt.workType = 'Change Requirement' THEN 'CR' WHEN wt.workType = 'Complaint' THEN 'CT' WHEN wt.workType = 'System Implimentation' THEN 'SI' WHEN wt.workType = 'System Design' THEN 'SD'
END AS Work_Type, wt.BUG_FIX_TYPE AS Bug_Fix_Type, wt.workDescription AS Work_Description, wt.workPriority AS Work_Priority, CASE WHEN wt.taskOverallStatus = 'ONGOING' THEN 'ONGOING' WHEN wt.taskOverallStatus = 'HOLD' THEN 'HOLD' WHEN wt.taskOverallStatus = 'COMPLETED' THEN 'COMPLETED' WHEN wt.taskOverallStatus = 'DEV.COMPLETED' THEN 'DEV.COMPD' WHEN wt.taskOverallStatus = 'DEV.RETURN' THEN 'DEV.RETURN' WHEN wt.taskOverallStatus = 'QA.COMPLETED' THEN 'QA.COMPD' WHEN wt.taskOverallStatus = 'QA.PENDING' THEN 'QA.PEND' WHEN wt.taskOverallStatus = 'NEW' THEN 'NEW' WHEN wt.taskOverallStatus = 'WITH DH' THEN 'WITH DH' WHEN wt.taskOverallStatus = 'RELEASED' THEN 'RELEASED' WHEN wt.taskOverallStatus = 'ALLOCATED' THEN 'ALLOCATED' END AS Task_Status,
ah.ASSIGNEE_ AS Pending_User
FROM worktask wt LEFT JOIN workallocationflow wa ON wt.workAllocation_ID = wa.id LEFT JOIN master_client mc ON wt.client_ID = mc.ID_ LEFT JOIN master_project mp ON wt.project_ID = mp.id LEFT JOIN master_job_type mj ON wt.fk_job_type = mj.ID LEFT JOIN act_id_user au ON wa.assignedDeveloper_ID = au.ID_ LEFT JOIN act_hi_taskinst ah ON wt.processinstance = ah.PROC_INST_ID_
wt.active=true
AND wt.submitionDate BETWEEN #{startDate} AND #{endDate}
AND wt.client_ID = #{clientId}
AND wt.project_ID = #{projectId}
AND wt.taskOverallStatus != 'COMPLETED'
AND wt.taskOverallStatus = #{overallStatus}
AND wa.assignedDeveloper_ID = #{developerId}
AND wt.workType = #{workType}
AND wt.chargeStatus = #{chargeType}
AND wt.fk_job_type = #{jobType}
AND wt.businesskey = #{wfRef}
AND ah.END_TIME_ IS NULL AND ah.ASSIGNEE_ = #{pendingUser}
